const item = $input.item.json;

console.log('Unify input received:', JSON.stringify(item, null, 2));

if (!item || !item.messageText) {
  console.log('ERROR: No messageText in input');
  return [{ json: { error: 'No message data', sourcePath: 'none' } }];
}

const unifiedItem = {
  ...item,
  sourcePath: item.sourcePath || (item.textFrom ? 'text' : 'voice'),
  messageText: item.messageText.trim(),
  hasValidMessage: true
};

console.log('Unified output:', JSON.stringify(unifiedItem, null, 2));

return [{ json: unifiedItem }];
