const input = $input.item.json;
const now = new Date();
let followUpDate = '';
let status = 'New';

if (input.leadQuality === 'HOT') {
  const followUp = new Date(now.getTime() + (4 * 60 * 60 * 1000));
  followUpDate = followUp.toISOString();
  status = 'Hot - Immediate Action';
} else if (input.leadQuality === 'WARM') {
  const followUp = new Date(now.getTime() + (24 * 60 * 60 * 1000));
  followUpDate = followUp.toISOString();
  status = 'Warm - Nurturing';
} else {
  const followUp = new Date(now.getTime() + (48 * 60 * 60 * 1000));
  followUpDate = followUp.toISOString();
  status = 'Cold - Long-term';
}

const leadData = {
  'Timestamp': input.timestamp || now.toISOString(),
  'Full Name': input.customerName || 'Unknown',
  'Phone Number': input.phoneNumber || '',
  'Business Activity': input.businessActivity || 'Not specified',
  'Target Market': input.targetMarket || 'Not specified',
  'Budget Range': input.budget || 'Not specified',
  'Visa Needs': input.visas || 'Not specified',
  'Timeline': input.timeline || 'Not specified',
  'Customer Message': input.messageText || '',
  'AI Response': input.aiResponse || '',
  'Lead Quality': input.leadQuality || 'COLD',
  'Status': status,
  'Follow Up Date': followUpDate,
  'Next Action': input.nextAction || 'NURTURE',
  'Needs Escalation': input.needsEscalation ? 'Yes' : 'No',
  'Response Source': input.responseSource || 'ai-agent',
  'Message ID': input.messageId || ''
};

return { json: leadData };

