const items = $input.all();
const now = new Date();
const results = [];

for (const item of items) {
  const data = item.json;
  if (data['Timestamp'] === 'Timestamp') continue;

  const leadQuality = data['Lead Quality'];
  const status = data['Status'];
  const followUpDate = data['Follow Up Date'];

  if ((leadQuality === 'HOT' || leadQuality === 'WARM') && 
      (status === 'New' || status.includes('Nurturing') || status.includes('Hot')) &&
      followUpDate) {
    const followUpTime = new Date(followUpDate);
    if (followUpTime <= now) {
      results.push({
        json: {
          customerName: data['Full Name'] || data['Customer Name'],
          phoneNumber: data['Phone Number'],
          lastMessage: data['Customer Message'] || data['Message'],
          businessActivity: data['Business Activity'],
          leadQuality: leadQuality,
          from: data['Phone Number'] + '@s.whatsapp.net'
        }
      });
    }
  }
}

return results;

for (const item of $input.all()) {
  if (item.binary) {
    const binaryPropertyNames = Object.keys(item.binary);
    for (const propName of binaryPropertyNames) {
      if (item.binary[propName].mimeType === 'audio/mp3') {
        item.binary[propName].mimeType = 'audio/mpeg';
      }
    }
  }
}
return $input.all();

const aiInput = $input.item.json;
console.log('Parse AI input:', JSON.stringify(aiInput, null, 2));

let aiResponse = '';
if (aiInput.output) {
  aiResponse = aiInput.output;
} else if (aiInput.text) {
  aiResponse = aiInput.text;
} else if (aiInput.message && aiInput.message.content) {
  aiResponse = aiInput.message.content;
} else if (aiInput.content) {
  aiResponse = aiInput.content;
} else {
  console.log('WARNING: No AI response found');
  aiResponse = 'No AI response available';
}

const lines = aiResponse.split('\n');
let responseText = '';
let leadQuality = 'COLD';
let nextAction = 'NURTURE';
let needsEscalation = false;
let businessActivity = '';
let targetMarket = '';
let budget = '';
let visas = '';
let timeline = '';

for (const line of lines) {
  if (line.includes('[LEAD_QUALITY:')) {
    const match = line.match(/\[LEAD_QUALITY:\s*(HOT|WARM|COLD)\]/i);
    if (match) leadQuality = match[1].toUpperCase();
  } else if (line.includes('[NEXT_ACTION:')) {
    const match = line.match(/\[NEXT_ACTION:\s*(\w+)\]/i);
    if (match) nextAction = match[1];
  } else if (line.includes('[ESCALATE_TO_HUMAN]')) {
    needsEscalation = true;
  } else if (line.includes('[BUSINESS_ACTIVITY:')) {
    const match = line.match(/\[BUSINESS_ACTIVITY:\s*([^\]]+)\]/i);
    if (match) businessActivity = match[1].trim();
  } else if (line.includes('[TARGET_MARKET:')) {
    const match = line.match(/\[TARGET_MARKET:\s*(Mainland|Free Zone|Both)\]/i);
    if (match) targetMarket = match[1];
  } else if (line.includes('[BUDGET:')) {
    const match = line.match(/\[BUDGET:\s*([^\]]+)\]/i);
    if (match) budget = match[1].trim();
  } else if (line.includes('[VISAS:')) {
    const match = line.match(/\[VISAS:\s*(\d+)\]/i);
    if (match) visas = match[1];
  } else if (line.includes('[TIMELINE:')) {
    const match = line.match(/\[TIMELINE:\s*(Immediate|1 Month|Exploring)\]/i);
    if (match) timeline = match[1];
  } else if (!line.startsWith('[')) {
    responseText += line + '\n';
  }
}

responseText = responseText.trim();

const unifyData = $('Unify Message Paths').item.json;

const output = {
  from: unifyData.from || aiInput.from || 'unknown',
  customerName: unifyData.customerName || aiInput.customerName || 'Customer',
  messageId: unifyData.messageId || aiInput.messageId || 'unknown',
  messageText: unifyData.messageText || aiInput.messageText || 'unknown',
  timestamp: unifyData.timestamp || aiInput.timestamp || $now.toISO(),
  phoneNumber: (unifyData.from || aiInput.from || '').replace('@s.whatsapp.net', ''),
  aiResponse: responseText,
  leadQuality: leadQuality,
  nextAction: nextAction,
  needsEscalation: needsEscalation,
  businessActivity: businessActivity,
  targetMarket: targetMarket,
  budget: budget,
  visas: visas,
  timeline: timeline,
  fromCache: false,
  responseSource: 'ai-agent',
  cacheKey: null,
  needsFollowUp: ['HOT', 'WARM'].includes(leadQuality)
};

console.log('Parse AI output:', JSON.stringify(output, null, 2));

return { json: output };

const item = $input.item.json;

console.log('Unify input received:', JSON.stringify(item, null, 2));

if (!item || !item.messageText) {
  console.log('ERROR: No messageText in input');
  return [{ json: { error: 'No message data', sourcePath: 'none' } }];
}

const unifiedItem = {
  ...item,
  sourcePath: item.sourcePath || (item.textFrom ? 'text' : 'voice'),
  messageText: item.messageText.trim(),
  hasValidMessage: true
};

console.log('Unified output:', JSON.stringify(unifiedItem, null, 2));

return [{ json: unifiedItem }];
